mapper.select:
select

from tbl_run_detail


		and `year` = #{year}


		and `month` = #{month}


order by year desc,month desc,day desc

select sum(kilometer) from tbl_run_detail
where run_date >= #{specialMonday} and run_date <= #{specialSunday}
group by run_date

SELECT sum(kilometer) kilometer, `year`, `month`, `day`, run_date FROM tbl_run_detail
where `year` =#{year} and `month` =#{month}
group BY DAY
